import React, {useRef} from "react";
import { useForm } from "react-hook-form";
import emailjs from '@emailjs/browser'

const Contact = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  // const onSubmit = (data, e) => {
  //   e.target.reset();
  //   console.log("Message submited: " + JSON.stringify(data));
  // };


  const form = useRef();
    const sendEmail = (e) => {
        e.preventDefault();

        emailjs.sendForm('service_quqlqqg', 'template_e5bdahn', form.current, 'VnuebFE-RHbLGu1qE')
            .then((result) => {
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            });
    };

  return (
      <>
          <form ref={form} onSubmit={sendEmail} className="contact_form">
              {/*<label>Name</label>*/}
              {/*<input type="text" name="user_name"/>*/}
              <label>Email</label>
              <input type="email" name="user_email"/>
              <label>Message</label>
              <textarea name="message"/>


                <div className="beny_tm_button">
                  <button type="submit" className="color" value="Send">
                    <span className="wrapper">
                      <span className="first">Submit</span>
                      <span className="second">Submit</span>
                    </span>
                  </button>
                </div>
          </form>


          {/*<form className="contact_form"*/}
          {/*      // onSubmit={handleSubmit(onSubmit)}*/}
          {/*      onSubmit={sendEmail}*/}
          {/*>*/}


          <div className="first_row">

              <input type="text" name="user_name"/>
              {/*<input {...register("name", { required: true })}*/}
              {/*  type="text"*/}
              {/*  placeholder="Name *"*/}
              {/*  name="user_name"*/}
              {/*/>*/}

              {errors.name && errors.name.type === "required" && (
                  <span className="invalid-feedback">Name is required</span>
              )}
          </div>


          {/*  <div className="second">*/}


        {/*    <div className="left">*/}
        {/*      <input*/}
        {/*          //        /!*{...register(*!/*/}
        {/*          //  /!*  "email",*!/*/}
        {/*          //        /!*  {*!/*/}
        {/*          //        /!*    required: "Email is Required",*!/*/}
        {/*          //       /!*    pattern: {*!/*/}
        {/*          //      /!*      value: /\S+@\S+\.\S+/,*!/*/}
        {/*          //      /!*      message: "Entered value does not match email format",*!/*/}
        {/*          //       /!*    },*!/*/}
        {/*          //       /!*  },*!/*/}
        {/*          //       /!*  { required: true }*!/*/}
        {/*          //      /!*)}*!/*/}
        {/*        type="email"*/}
        {/*        placeholder="Email *"*/}
        {/*        name="user_email"*/}
        {/*      />*/}
        {/*      {errors.email && (*/}
        {/*        <span className="invalid-feedback">{errors.email.message}</span>*/}
        {/*      )}*/}
        {/*    </div>*/}



        {/*    <div className="right">*/}
        {/*      <input*/}
        {/*          //     /!*{...register("subject", { required: true })}*!/*/}
        {/*        placeholder="Subject *"*/}
        {/*      />*/}
        {/*      {errors.subject && (*/}
        {/*        <span className="invalid-feedback">Subject is required.</span>*/}
        {/*      )}*/}
        {/*    </div>*/}
        {/*  </div>*/}



        {/*  <div className="third">*/}
        {/*    <textarea*/}
        {/*        //     /!*{...register("message", { required: true })}*!/*/}
        {/*      placeholder="Message *"*/}
        {/*      name="message"*/}
        {/*    ></textarea>*/}
        {/*    {errors.message && (*/}
        {/*      <span className="invalid-feedback">Message is required.</span>*/}
        {/*    )}*/}
        {/*  </div>*/}


        {/*  <div className="beny_tm_button">*/}
        {/*    <button type="submit" className="color" value="Send">*/}
        {/*      <span className="wrapper">*/}
        {/*        <span className="first">Submit</span>*/}
        {/*        <span className="second">Submit</span>*/}
        {/*      </span>*/}
        {/*    </button>*/}
        {/*  </div>*/}

        {/*</form>*/}
      </>
  );
};

export default Contact;
